mapper.update:
UPDATE tbl_parent_safestudy SET totalscore=#{totalScore}
 WHERE safeid=#{safeId}
 AND pid=#{pid}

mapper.select:
SELECT qu.*,ps.myanswer
FROM tbl_parent_safestudy ps,tbl_question qu
WHERE ps.questionid=qu.questionid
AND ps.pid=#{pid}
AND ps.safeid=#{safeId}

SELECT *
FROM tbl_question
WHERE questionid=#{questionId}

SELECT COUNT(wa.warnid)
FROM tbl_warning wa,tbl_baby ba
WHERE wa.bid=ba.bid

    AND area like '%' #{area} '%'


    AND warntime >= #{startDate}


    AND warntime <= #{endDate}

SELECT wa.*,ba.bname
FROM tbl_warning wa,tbl_baby ba
WHERE wa.bid=ba.bid

        AND area like '%' #{area} '%'


        AND warntime >= #{startDate}


        AND warntime <= #{endDate}

LIMIT #{pstart},#{psize}

SELECT cl.cname,ba.bid,ba.bname,pa.pname,pa.pid FROM tbl_classroom cl,tbl_baby ba,tbl_parent pa,tbl_parent_baby pb WHERE cl.cid=#{cid}
AND ba.bname like '%' #{bName} '%'
AND cl.cid=ba.cid AND ba.bid=pb.bid AND pb.pid=pa.pid LIMIT #{pstart},#{psize}

SELECT COUNT(ba.bid) FROM tbl_classroom cl,tbl_baby ba,tbl_parent pa,tbl_parent_baby pb WHERE cl.cid=#{cid}
AND ba.bname like '%' #{bName} '%'
AND cl.cid=ba.cid AND ba.bid=pb.bid AND pb.pid=pa.pid

SELECT COUNT(sa.safeid) FROM tbl_safestudy sa LEFT JOIN (SELECT DISTINCT ps.pid,ps.safeid,ps.totalscore FROM tbl_parent_safestudy ps WHERE ps.pid=#{pid}) psa ON sa.safeid=psa.safeid
AND safename like '%' #{safeName} '%'
AND safedate >= #{startDate}
AND safedate <= #{endDate}

SELECT sa.*,psa.totalscore
FROM tbl_safestudy sa
LEFT JOIN (SELECT DISTINCT ps.pid,ps.safeid,ps.totalscore FROM tbl_parent_safestudy ps WHERE ps.pid=#{pid}) psa
ON sa.safeid=psa.safeid


        AND safename like '%' #{safeName} '%'


        AND safedate >= #{startDate}


        AND safedate <= #{endDate}


LIMIT #{pstart},#{psize}

SELECT *
FROM tbl_safestudy


        AND safename like '%' #{safeName} '%'


        AND safedate >= #{startDate}


        AND safedate <= #{endDate}


LIMIT #{pstart},#{psize}

SELECT COUNT(safeid)
FROM tbl_safestudy


        AND safename like '%' #{safeName} '%'


        AND safedate >= #{startDate}


        AND safedate <= #{endDate}

SELECT tbl_parent.ppsw
FROM tbl_parent
WHERE tbl_parent.pid=#{pid}

SELECT co.couid,sub.subname,co.cdate,co.corder,sub.subid
FROM tbl_baby ba,tbl_course co,tbl_subject sub
WHERE ba.bid=#{bid}
AND ba.cid=co.cid
AND co.subid = sub.subid
AND co.cdate >= #{startDate}
AND co.cdate <= #{endDate}

SELECT cl.cid,cl.cname,wo.wname,cl.classroom,cl.courseadddate
FROM tbl_classroom cl,tbl_worker wo
WHERE cl.wid=wo.wid
AND wo.wid=#{wid}

    AND cl.cname like '%' #{cName} '%'


    AND cl.courseadddate >= #{startDate}


    AND cl.courseadddate <= #{endDate}

LIMIT #{pstart},#{psize}

SELECT COUNT(cl.cid)
FROM tbl_classroom cl,tbl_worker wo
WHERE cl.wid=wo.wid
AND wo.wid=#{wid}

    AND cl.cname like '%' #{cName} '%'


    AND cl.courseadddate >= #{startDate}


    AND cl.courseadddate <= #{endDate}

SELECT co.couid,sub.subname,co.cdate,co.corder,sub.subid
FROM tbl_course co,tbl_subject sub,tbl_classroom cl
WHERE co.subid = sub.subid
AND cl.wid=#{wid}
AND cl.cid=#{cid}
AND cl.cid=co.cid
AND co.cdate >= #{startDate}
AND co.cdate <= #{endDate}

SELECT co.couid,sb.subname,co.cdate,co.corder,sb.subid
FROM tbl_course co,tbl_subject sb,tbl_classroom cl
WHERE co.subid=sb.subid
AND co.cid=cl.cid
AND cl.cid=#{cid}
AND co.cdate >= #{startDate}
AND co.cdate <= #{endDate}

SELECT cl.cid,cl.cname,wo.wname,cl.classroom,cl.courseadddate
FROM tbl_classroom cl,tbl_worker wo
WHERE cl.wid=wo.wid

        AND cl.cname like '%' #{cName} '%'


        AND cl.courseadddate >= #{startDate}


        AND cl.courseadddate <= #{endDate}

LIMIT #{pstart},#{psize}

SELECT COUNT(cl.cid)
FROM tbl_classroom cl,tbl_worker wo
WHERE cl.wid=wo.wid

    AND cl.cname like '%' #{cName} '%'


    AND cl.courseadddate >= #{startDate}


    AND cl.courseadddate <= #{endDate}

SELECT l.lid,l.ltime,l.ldate,l.levent,wo.wname FROM tbl_log l,tbl_worker wo
WHERE l.wid=wo.wid

        AND wo.wname like '%' #{wName} '%'


        AND l.ldate >= #{startDate}


        AND l.ldate <= #{endDate}

ORDER BY l.ldate DESC,l.ltime DESC
LIMIT #{pstart},#{psize}

SELECT COUNT(l.lid) FROM tbl_log l,tbl_worker wo
WHERE l.wid=wo.wid

    AND wo.wname like '%' #{wName} '%'


    AND l.ltime >= #{startDate}


    AND l.ltime <= #{endDate}
